(41, 1, 0, 'feedback', '', 0, 13, 0, '<div class=\"modal fade\" id=\"feedback\" tabindex=\"-1\" role=\"dialog\" aria-labelledby=\"ModalLabel\" aria-hidden=\"true\">\n  <div class=\"modal-dialog modal-sm\" role=\"document\">\n    <div class=\"modal-content\">\n      <div class=\"modal-header\">\n        <h5 class=\"modal-title\" id=\"ModalLabel\">Обратная связь</h5>\n        <button type=\"button\" class=\"close\" data-dismiss=\"modal\" aria-label=\"Close\">\n          <span aria-hidden=\"true\">&times;</span>\n        </button>\n      </div>\n      <div class=\"modal-body\">\n        <form>\n          <div class=\"form-group\">\n            <label for=\"your_name\" class=\"col-form-label\">Как мы можем к вам обращаться?</label>\n            <input type=\"text\" class=\"form-control\" id=\"your_name\" placeholder=\"ФИО\">\n          </div>\n          <div class=\"form-group\">\n            <label for=\"your_phone\" class=\"col-form-label\">Телефон</label>\n            <input type=\"text\" class=\"form-control\" id=\"your_phone\" placeholder=\"Введите номер телефона\">\n          </div>\n          <div class=\"form-group\">\n            <label for=\"your_email\" class=\"col-form-label\">E-mail</label>\n            <input type=\"text\" class=\"form-control\" id=\"your_email\" placeholder=\"Введите адрес электронной почты\">\n          </div>\n         <div class=\"form-group\">\n            <label for=\"your_theme\">Интересующая тематика</label>\n            <select class=\"form-control\" id=\"your_theme\">\n              <option disabled selected>Выберите интересуюшую вас тему</option>\n              <option>Комплеанс</option>\n              <option>2</option>\n              <option>3</option>\n            </select>\n          </div>\n          <div class=\"form-group\">\n            <label for=\"your_message\" class=\"col-form-label\">Ващ вопрос</label>\n            <textarea class=\"form-control\" id=\"your_message\" rows=\"3\" placeholder=\"Напишите свой вопрос\"></textarea>\n          </div>\n        </form>\n      </div>\n      <div class=\"modal-footer\">\n        <button type=\"button\" class=\"send_button\">Отправить сообщение</button>\n      </div>\n    </div>\n  </div>\n</div>', 0, 'a:0:{}', 0, '');
